import React from 'react'
import { useState } from 'react'

const Form = () => {
  const [description, setDescription] = useState('');
  //created a state called description to track the description and a funcction called setDescription to change the state and set the initial value to an empty string.
  return (
    <div className='form'>
        <form >
             <label>Date</label>
             <input type="text" 
             />
             <input type="text" placeholder='Description' value={description} 
              onChange={(e) => setDescription(e.target.value)}
              // allows a change by triggering the setDescription function and updates it with whatever the user is trying to change it to.
              // the update occurs by accessing the event o
             />
             {/* value is equal to a dynamic value which is the description state*/}
             <input type="text" placeholder='Category'/>
             <input type="text" placeholder='Amount' />
        </form>
    </div>
  )
}

export default Form
